definition.script:
node {
    wrap([$class: 'TimestamperBuildWrapper']) {
        timeout(time: 12, unit: 'HOURS') {
            stage('Cleanup') {
                deleteDir()
            }
            ws {
                dir('site') {
                    stage('Checkout site.conf') {
                        checkout([$class: 'GitSCM', userRemoteConfigs: [[url: 'https://github.com/chris1911/site-ffka']], branches: [[name: 'refs/tags/v0.2.90-stable.1']]])
                    }

                    def gluonBranch = sh([returnStdout: true, script: 'make -f jenkins.mk'])
                    echo gluonBranch
                }
            }
        }
    }
}
