definition.script:
node {
    stage("Stage Parallel") {
        def branches = [:]
        for (int i = 0; i < 5; i++) {
            branches["split${i}"] = {
                stage("Stage parallel- #"+i) {
                    node {
                        echo  'Starting sleep'
                        sleep 10
                        echo  'Finished sleep'
                    }
                }
            }
        }
        parallel branches
    }
}
